mapper.update:
update TB_BOARD set BOARD_VIEWS = BOARD_VIEWS + 1
where BOARD_ID =
#{board_id};

mapper.delete:
delete from TB_BOOKMARK where CUST_ID = #{cust_id} and
BOARD_ID =
#{board_id} ;

mapper.select:
select * from TB_BOARD
where BOARD_ID IN ( select BOARD_ID
from TB_BOARD_SKILL where SKILL_NAME = #{skill_name} )
order by BOARD_DT
limit
#{endpoint}, 6;
