mapper.update:

update TB_BOARD set BOARD_VIEWS = BOARD_VIEWS + 1	
where BOARD_ID =	
#{board_id};

mapper.select:

select * from TB_BOARD	
where BOARD_ID IN ( select BOARD_ID	
from TB_BOARD_SKILL where SKILL_NAME = #{skillname} )	
order by BOARD_DT	
limit	
#{endpoint}, 6;


select * from TB_BOARD where BOARD_ID 	
in ( select BOARD_ID from TB_APPLY	
where CUST_ID = #{cust_id}) ;
